import React, { useState, useEffect } from "react";
import Rate from "./Rate";

export const Product = ({ product, selectedProductId }) => {
  const [productClassName, setProductClassName] = useState("");
  useEffect(() => {
    if (product.id == selectedProductId) {
      setProductClassName("product-detail");
    } else {
      setProductClassName("product-detail product-hide");
    }
  }, [selectedProductId]);
  return (
    <div>
      <div className={productClassName}>
        <div className="product-price">${product.price}</div>
        <div className="product-description">{product.description}</div>
        <Rate />
        <button className="product-add-card">Add to Card</button>
      </div>
    </div>
  );
};
export default Product;
